function fourier = fourier (gk, N)    wn                = exp(-1i*2*pi/N);                  % Definindo wn  Matriz_jotas      = wn*ones(N, N);                    % Matriz NxN wn  Matriz_expoentes  = [0:1:N-1]'*[0:1:N-1];             % Expoentes de wn  Wn                = Matriz_jotas.^Matriz_expoentes;   % matriz wn^(expoentes)  Xn                = Wn*gk;                            % Calculo da matriz Xn    fourier = fftshift(log10(abs(Xn)));endfunction